CREATE TABLE `pengarang` ( `id` int(11) NOT NULL AUTO_INCREMENT, `nama` varchar(50) DEFAULT NULL, `email` varchar(25) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=latin1;
CREATE TABLE `buku` ( `id` int(11) NOT NULL AUTO_INCREMENT, `isbn` varchar(15) DEFAULT NULL, `judul` varchar(50) DEFAULT NULL, `idpengarang` int(50) DEFAULT NULL, `stok` int(11) DEFAULT NULL, `gambar` varchar(100) DEFAULT NULL, PRIMARY KEY (`id`), KEY `fk_pegarang` (`idpengarang`), CONSTRAINT `fk_pegarang` FOREIGN KEY (`idpengarang`) REFERENCES `pengarang` (`id`) ) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=latin1
